mapper.select:
		select
    		c.`id`,
    		c.`mobile`,
    		c.`client_name`,
    		c.`wechat_no`,
    		c.`img_url`,
    		c.`client_type`,
			c.`channel`,
			c.`register_time`
		from t_client c,t_merchant m
		WHERE c.merchant_id = m.id

			AND c.id = #{id}


			AND c.client_name LIKE concat('%',#{name},'%')


			AND c.client_type  = #{clientType}


			AND c.merchant_id = #{merchantId}


			AND c.up_client_id = #{upClientId}


             and date(c.register_time) = date(#{registerTime,jdbcType=TIMESTAMP})



                order by ${sidx} ${order}


                order by c.id desc



			limit #{offset}, #{limit}

		select count(*)
		from t_client c,t_merchant m
		WHERE c.merchant_id = m.id

            AND c.client_name LIKE concat('%',#{name},'%')


			AND c.client_type  = #{clientType}


			AND c.merchant_id = #{merchantId}


			AND c.up_client_id = #{upClientId}


             and date(c.register_time) = date(#{registerTime,jdbcType=TIMESTAMP})


             and date(c.last_login_time) = date(#{lastLoginTime,jdbcType=TIMESTAMP})

mapper.update:
update t_client t
t.`client_name` = #{clientName}, t.`wechat_no` = #{wechatNo}, t.`mobile` = #{mobile}, t.`img_url` = #{imgUrl}, t.`client_type` = #{clientType}, t.`channel` = #{channel}, t.`register_time` = #{registerTime}
where t.id = #{id}
