mapper.select:
select c.id, e.img_url, e.mobile, c.product_id as productId, c.client_id as clientId, e.client_name as clientName, c.score, c.description, c.create_time, c.modify_time, c.creator, c.modifier from t_evaluate c,t_client e where c.client_id = e.id
AND c.score LIKE concat('%',#{name},'%')
AND c.merchant_id = #{merchantId}
order by ${sidx} ${order}
order by c.id desc
limit #{offset}, #{limit}

select count(*) from t_evaluate c WHERE 1=1
AND c.score LIKE concat('%',#{name},'%')
AND c.merchant_id = #{merchantId}

mapper.update:
update t_evaluate
merchant_id = #{merchantId}, product_id = #{productId}, client_id = #{clientId}, score = #{score}, description = #{description}, create_time = #{createTime}, modify_time = #{modifyTime}, creator = #{creator}, modifier = #{modifier}
where id = #{id}
