mapper.select:
select t_product.id, t_product.buy_status, t_product.unit, t_product.product_name, t_product.category_id, t_category.category_name, t_product.vip_price, t_product.price, t_product.count_num, t_product.tag, t_product.description, t_product.status, t_product.create_time, t_product.modify_time, t_product.creator, t_product.modifier from t_product LEFT JOIN t_category ON t_category.id = t_product.category_id WHERE 1=1
AND t_product.product_name LIKE concat('%',#{name},'%')
AND t_product.category_id = #{categoryId}
AND t_category.merchant_id = #{merchantId}
order by ${sidx} ${order}
order by t_product.id desc
limit #{offset}, #{limit}

select count(*) from t_product LEFT JOIN t_category ON t_category.id = t_product.category_id WHERE 1=1
AND t_product.product_name LIKE concat('%',#{name},'%')
AND t_product.category_id = #{categoryId}
AND t_category.merchant_id = #{merchantId}
